import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators} from '@angular/forms';

@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.scss']
})
export class RegistrationComponent {
  registration: FormGroup;

  constructor(private fb: FormBuilder) {
    this.createForm();
  }

  createForm() {
    this.registrationForm = this.fb.group({
      username: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });
  }

  onSubmit() {
    console.log(this.registrationForm.value);
  }
}
<p>registration works!</p>

<form [formGroup]="registrationForm" (ngSubmit)="onSubmit()">
    <div>
      <label>Username</label>
      <input type="text" formControlName="username">
      <div *ngIf="registrationForm.get('username').invalid && (registrationForm.get('username').dirty || registrationForm.get('username').touched)">
        <div *ngIf="registrationForm.get('username').errors.required">Username is required.</div>
      </div>
    </div>

    <div>
      <label>Email</label>
      <input type="email" formControlName="email">
      <div *ngIf="registrationForm.get('email').invalid && (registrationForm.get('email').dirty || registrationForm.get('email').touched)">
        <div *ngIf="registrationForm.get('email').errors.required">Email is required.</div>
        <div *ngIf="registrationForm.get('email').errors.email">Invalid email format.</div>
      </div>
    </div>

    <div>
      <label>Password</label>
      <input type="password" formControlName="password">
      <div *ngIf="registrationForm.get('password').invalid && (registrationForm.get('password').dirty || registrationForm.get('password').touched)">
        <div *ngIf="registrationForm.get('password').errors.required">Password is required.</div>
      </div>
    </div>

    <div>
      <label>Confirm Password</label>
      <input type="password" formControlName="confirmPassword">
      <div *ngIf="registrationForm.get('confirmPassword').invalid && (registrationForm.get('confirmPassword').dirty || registrationForm.get('confirmPassword').touched)">
        <div *ngIf="registrationForm.get('confirmPassword').errors.required">Confirm Password is required.</div>
      </div>
    </div>

    <button type="submit" [disabled]="!registrationForm.valid">Register</button>
  </form>
