import { Component } from '@angular/core';
import { AuthService } from '../auth/auth.service';
import {FormControl, Validators} from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {

  email = new FormControl('',[Validators.required, Validators.email]);
  password = new FormControl('',[Validators.required]);

  getErrorMessage() {
    if (this.email.hasError('required')) {
      return 'Заполните поле';
    }

    return this.email.hasError('email') ? "Не верный формат" : '';
  }

  constructor(private authSevice: AuthService) {}

  register(email: string, password: string)
  {
    this.authSevice.register(email, password);
  }
}
<p>register works!</p>

<form class="mb-3" (submit)="register">
  <div class="mb-3">
    <label for="" class="form-label">Email</label>
    <input type="email" class="form-control" name="" id="" aria-describedby="emailHelpId" placeholder="abc@mail.com">
    <small id="emailHelpId" class="form-text text-muted">Help text</small>
  </div>

  <div class="mb-3">
    <label for="" class="form-label">Password</label>
    <input type="password" class="form-control" name="" id="" placeholder="">
  </div>

  <input name="" id="" class="btn btn-primary" type="submit" value="Button">
</form>

<div class="register">
  <mat-form-field appearance="fill">
    <mat-label>Enter your email</mat-label>
    <input matInput placeholder="pat@example.com" [formControl]="email" required>
    <mat-error *ngIf="email.invalid">{{getErrorMessage()}}</mat-error>
  </mat-form-field>
</div>
