import { Component } from '@angular/core';
import { AuthService } from '../auth/auth.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  form: any = {
    username: null,
    email: null,
    password: null
  };
  isSuccessful = false;
  isSignUpFailed = false;
  errorMessage = '';

  constructor(private authService: AuthService) { }

  register(): void {
    const { email, password } = this.form;

    this.authService.register( email, password).subscribe(
      data => {
        console.log(data);
        this.isSuccessful = true;
        this.isSignUpFailed = false;
      },
      err => {
        this.errorMessage = err.error.message;
        this.isSignUpFailed = true;
      }
    );
  }
}
<h1 class="text-center mt-5 mb-5">Регистрация</h1>

<section class="vh-100">
  <div class="container-fluid h-custom">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-md-9 col-lg-6 col-xl-5 mb-5">
        <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp"
          class="img-fluid" alt="Sample image">
      </div>
      <div class="col-md-8 col-lg-6 col-xl-4 offset-xl-1">

        <form [formGroup]="registrationForm" (ngSubmit)="register">

          <div class="form-outline mb-4">
            <label class="form-label" for="form3Example3">Email</label>
            <input type="email" id="form3Example3" class="form-control form-control-lg"
              placeholder="example@example.com" formControlName="email"/>
          </div>

          <div class="form-outline mb-3">
            <label class="form-label" for="form3Example4">Пароль</label>
            <input type="password" id="form3Example4" class="form-control form-control-lg"
              placeholder="" formControlName="password" />
          </div>

          <div class="d-flex justify-content-between align-items-center">

            <div class="form-check mb-0">
              <input class="form-check-input me-2" type="checkbox" id="form2Example3" />
              <label class="form-check-label" for="form2Example3">
                Запомнить меня
              </label>
            </div>
            <a href="#!" class="text-body">Забыли пароль?</a>
          </div>

          <div class="text-center text-lg-start mt-4 pt-2">
            <button type="submit" class="btn btn-primary btn-lg"
              style="padding-left: 2.5rem; padding-right: 2.5rem;">Регистрация</button>
            <p class="small fw-bold mt-2 pt-1 mb-0">У вас уже есть аккаунт? <a href="#!"
                class="link-danger">Войдите в него!</a></p>
          </div>
        </form>
      </div>
    </div>
  </div>

</section>
